char* func(char* d) {

  char *a = "";

  strcpy(d, a );
  return d;
}

char* outer() {
  char dst[4] = "bbb";
  return func(dst);
}


int main(int argc, char *argv[])
{

  int i = 0;
  char *ret;
  for( ;i < 10; i++){
    printf("%d: %s\n", i, ret);
    ret = outer();
  }
	

  return 0;
}
















